class SwapiService {

  _apiBase = 'https://swapi.co/api/';

  async getResourse(url) {
    const res = await fetch (`${this._apiBase}${url}`);

    if(!res.ok) {
      throw new Error(`Couldn't fetch ${url}` + `, recieved ${res.status}`)
    }

    return await res.json()
  };

  getAllPeople() {
    return this.getResourse()
  }
}
